CREATE TABLE IF NOT EXISTS `sights` (
  `sights_id` int(4) NOT NULL AUTO_INCREMENT,
  `sights_name` varchar(50) NOT NULL,
  `city` varchar(50) NOT NULL,
  `address` varchar(50) NOT NULL,
  `intro` text NOT NULL,
  `ticket` varchar(50) NOT NULL,
  PRIMARY KEY (`sights_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=8 ;

--
-- 转存表中的数据 `sights`
--

INSERT INTO `sights` (`sights_id`, `sights_name`, `city`, `address`, `intro`, `ticket`) VALUES
(5, '武功山', '江西省萍乡市', '江西省萍乡市芦溪县164县道', '<p style="text-align:center"><img src="https://timg01.bdimg.com/timg?wapbaike&quality=80&size=b500_330&sec=1349839550&di=b01b5c4de6580ce813911914888406d7&src=http://imgsrc.baidu.com/baike/pic/item/77c6a7efce1b9d16613c46eafadeb48f8d546446.jpg"/></p><p style="margin-top: 0px; margin-bottom: 0px; color: rgb(63, 63, 63); font-family: Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">武功山位于中国江西省中西部，居<a href="https://wapbaike.baidu.com/item/%E7%BD%97%E9%9C%84%E5%B1%B1%E8%84%89" style="color: rgb(58, 127, 222); text-decoration: none;">罗霄山脉</a>北支，山体呈东北——西南走向，地跨江西萍乡市芦溪县、吉安市安福县，宜春市袁州区，主脉绵延120余千米，总面积约970平方千米。</p><p style="margin-top: 0px; margin-bottom: 0px; color: rgb(63, 63, 63); font-family: Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">武功山围绕金顶已设立“<a href="https://wapbaike.baidu.com/item/%E6%AD%A6%E5%8A%9F%E5%B1%B1%E5%9B%BD%E5%AE%B6%E5%9C%B0%E8%B4%A8%E5%85%AC%E5%9B%AD" style="color: rgb(58, 127, 222); text-decoration: none;">武功山国家地质公园</a>”（面积约378.3平方千米）、“<a href="https://wapbaike.baidu.com/item/%E6%AD%A6%E5%8A%9F%E5%B1%B1%E5%9B%BD%E5%AE%B6%E6%A3%AE%E6%9E%97%E5%85%AC%E5%9B%AD" style="color: rgb(58, 127, 222); text-decoration: none;">武功山国家森林公园</a>”（面积约260平方千米）、“<a href="https://wapbaike.baidu.com/item/%E6%98%8E%E6%9C%88%E5%B1%B1%E5%9B%BD%E5%AE%B6%E6%A3%AE%E6%9E%97%E5%85%AC%E5%9B%AD" style="color: rgb(58, 127, 222); text-decoration: none;">明月山国家森林公园</a>”（面积约136平方千米）。武功山主峰<a href="https://wapbaike.baidu.com/item/%E7%99%BD%E9%B9%A4%E5%B3%B0" style="color: rgb(58, 127, 222); text-decoration: none;">白鹤峰</a>（金顶）海拔1918.3米。自汉晋起，武功山被道佛两家择为修身养性之洞天福地，明朝时香火达到鼎盛时期，山南山北建进了庵、堂、寺、观达100多处。自唐宋以来，诸多仰慕其名而登山游赏吟诗作赋的名人学士络绎不绝。武功山有山岳型国家重点风景名胜区、<a href="https://wapbaike.baidu.com/item/%E5%9B%BD%E5%AE%B6AAAA%E7%BA%A7%E6%97%85%E6%B8%B8%E6%99%AF%E5%8C%BA" style="color: rgb(58, 127, 222); text-decoration: none;">国家AAAA级旅游景区</a>、<a href="https://wapbaike.baidu.com/item/%E5%9B%BD%E5%AE%B6%E7%BA%A7%E9%A3%8E%E6%99%AF%E5%90%8D%E8%83%9C%E5%8C%BA" style="color: rgb(58, 127, 222); text-decoration: none;">国家级风景名胜区</a>、<a href="https://wapbaike.baidu.com/item/%E5%9B%BD%E5%AE%B6%E6%A3%AE%E6%9E%97%E5%85%AC%E5%9B%AD" style="color: rgb(58, 127, 222); text-decoration: none;">国家森林公园</a>、<a href="https://wapbaike.baidu.com/item/%E5%9B%BD%E5%AE%B6%E5%9C%B0%E8%B4%A8%E5%85%AC%E5%9B%AD" style="color: rgb(58, 127, 222); text-decoration: none;">国家地质公园</a>、<a href="https://wapbaike.baidu.com/item/%E5%9B%BD%E5%AE%B6%E8%87%AA%E7%84%B6%E9%81%97%E4%BA%A7" style="color: rgb(58, 127, 222); text-decoration: none;">国家自然遗产</a>、中国青少年户外体育运动营地等。</p><p style="margin-top: 0px; margin-bottom: 0px; color: rgb(63, 63, 63); font-family: Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">2009年，武功山被<a href="https://wapbaike.baidu.com/item/%E4%B8%AD%E5%9B%BD%E5%9B%BD%E5%AE%B6%E5%9C%B0%E7%90%86" style="color: rgb(58, 127, 222); text-decoration: none;">中国国家地理</a>评为中国十大“非著名”山峰之一。2017年1月，江西省出台《武功山经营管理体制改革方案》，明确将对武功山进行统一规划、统一整合、统一经营。</p><p><br/></p>', '70元'),
(6, '仙女湖', '新余市', '江西省新余市西南郊16公里处', '<p>仙女湖风景名胜区是国家重点风景名胜区，位于江西省西部的新余市，因东晋文学家干宝的《搜神记》中“毛衣女”下凡豫章新喻县的神话传说而得名。仙女湖地处江西省“三清山—龙虎山—南昌—仙女湖—萍乡”和“庐山—鄱阳湖—南昌—仙女湖—井冈山—赣州”两条旅游线路的交汇点，区位条件优越。</p><p>仙女湖风景名胜区分为舞龙湖景区、钟山峡景区、钤阳湖景区、大岗山景区、九龙湾景区五个部分。这里人文荟萃，有古溶洞、古建筑、水下古城、古墓、石刻、古代名著、革命历史遗址、渔家风情等人文景观。《天仙配》有文字记载的最早神话传说、江西第一个状元卢肇、明朝权相严嵩、古代科技巨著《天工开物》、现代国画大师傅抱石等就诞生在这里；这里气候宜人，仙女湖是典型的亚热带气候，四季分明，日照充足，雨量充沛，无霜期长，年平均气温 17 ℃左右。这里风光秀美，仙女湖最大的特点是：青山绿水、山水相映、水天一色。有植物230科、 3000多种，其中名贵树种200多种，森林覆盖率达95%以上。水质达地表水二级标准，能见度达2米以上，良好的生态环境滋养了众多的动物，有鹿、狐、猴等兽类动物37种，鹰、隼、雁、白鹭、斑鸠等鸟类动物39种。每到清晨与傍晚，都能见到湖光山色之间百鸟翔集的美景，是人、动物和大自然和谐共处的天堂。</p><p style="text-align: center;"><img src="https://gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/54fbb2fb43166d22eea3b546442309f79152d208.jpg" alt="仙女湖图片"/></p>', '160元'),
(7, '仙女湖', '新余市', '江西省新余市西南郊16公里处', '<p>仙女湖风景名胜区是国家重点风景名胜区，位于江西省西部的新余市，因东晋文学家干宝的《搜神记》中“毛衣女”下凡豫章新喻县的神话传说而得名。仙女湖地处江西省“三清山—龙虎山—南昌—仙女湖—萍乡”和“庐山—鄱阳湖—南昌—仙女湖—井冈山—赣州”两条旅游线路的交汇点，区位条件优越。</p><p>仙女湖风景名胜区分为舞龙湖景区、钟山峡景区、钤阳湖景区、大岗山景区、九龙湾景区五个部分。这里人文荟萃，有古溶洞、古建筑、水下古城、古墓、石刻、古代名著、革命历史遗址、渔家风情等人文景观。《天仙配》有文字记载的最早神话传说、江西第一个状元卢肇、明朝权相严嵩、古代科技巨著《天工开物》、现代国画大师傅抱石等就诞生在这里；这里气候宜人，仙女湖是典型的亚热带气候，四季分明，日照充足，雨量充沛，无霜期长，年平均气温 17 ℃左右。这里风光秀美，仙女湖最大的特点是：青山绿水、山水相映、水天一色。有植物230科、 3000多种，其中名贵树种200多种，森林覆盖率达95%以上。水质达地表水二级标准，能见度达2米以上，良好的生态环境滋养了众多的动物，有鹿、狐、猴等兽类动物37种，鹰、隼、雁、白鹭、斑鸠等鸟类动物39种。每到清晨与傍晚，都能见到湖光山色之间百鸟翔集的美景，是人、动物和大自然和谐共处的天堂。</p><p style="text-align: center;"><img src="https://gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/54fbb2fb43166d22eea3b546442309f79152d208.jpg" alt="仙女湖图片"/></p>', '160元');

